declare module "*.css" {
  const styles: any
  export = styles
}

declare module "*.scss" {
  const styles: any
  export = styles
}

declare module "*.json" {
  const value: any
  export = value
}

declare module "*.jpg"
declare module "*.jpeg"
declare module "*.png"
declare module "*.svg"

declare module "react-syntax-highlighter"
declare module "react-syntax-highlighter/dist/languages/hljs/xml"
declare module "react-syntax-highlighter/dist/styles/hljs/github"
