@import 'lib/border';
@import 'lib/theme';

$pds-focus-offset-map: (
  'small': 2px,
  'none': 0,
);

$pds-focus-border-radius-map: (
  'small': $pds-border-radius-small,
  'medium': $pds-border-radius-medium,
);

@mixin pds-focus($offset: 'small', $border-radius: 'small') {
  // it can easily be overwritten on purpose (when placed here) and visually reflected
  @if map-has-key($pds-focus-border-radius-map, $border-radius) {
    border-radius: map-get($pds-focus-border-radius-map, $border-radius);
  } @else {
    @if ($border-radius) {
      border-radius: $border-radius;
    } @else {
      border-radius: map-get($pds-focus-border-radius-map, 'none');
    }
  }
  &:focus {
    outline: $pds-border-width-base solid $pds-theme-light-state-focus;
    @if map-has-key($pds-focus-offset-map, $offset) {
      outline-offset: map-get($pds-focus-offset-map, $offset);
    } @else {
      @if ($offset) {
        outline-offset: $offset;
      } @else {
        outline-offset: map-get($pds-focus-offset-map, 'small');
      }
    }
  }
  // why? have a look at this article https://developer.paciellogroup.com/blog/2018/03/focus-visible-and-backwards-compatibility/
  &:focus:not(:focus-visible) {
    outline-color: transparent;
  }
}

@import 'lib/border';
// @import 'lib/frosted-glass';
@import 'lib/theme';
@import 'lib/motion';

$pds-focus-border-radius-map: (
  'small': $pds-border-radius-small,
  'medium': $pds-border-radius-medium,
);

$pds-offset-horizontal: 2px;

@mixin pds-hover($border-radius: 'small') {
  // it can easily be overwritten on purpose (when placed here) and visually reflected
  @if map-has-key($pds-focus-border-radius-map, $border-radius) {
    border-radius: map-get($pds-focus-border-radius-map, $border-radius);
  } @else {
    @if ($border-radius) {
      border-radius: $border-radius;
    } @else {
      border-radius: map-get($pds-focus-border-radius-map, 'small');
    }
  }
  margin-left: -#{$pds-offset-horizontal};
  margin-right: -#{$pds-offset-horizontal};
  padding-left: $pds-offset-horizontal;
  padding-right: $pds-offset-horizontal;
  transition: background var(--p-transition-duration, $pds-motion-duration-short) $pds-motion-easing-base;
  @media (hover: hover) {
    &:hover {
      // @include pds-frosted-glass;
      background: $pds-theme-light-state-hover;
    }
  }
}

@import 'lib/_breakpoint.scss';

$pds-breakpoints: (
  'base': $pds-breakpoint-base,
  'xs': $pds-breakpoint-xs,
  's': $pds-breakpoint-s,
  'm': $pds-breakpoint-m,
  'l': $pds-breakpoint-l,
  'xl': $pds-breakpoint-xl,
  'xxl': $pds-breakpoint-xxl,
);

@mixin pds-media-query-min($min: null) {
  @if map-has-key($pds-breakpoints, $min) {
    @media (min-width: #{map-get($pds-breakpoints, $min)}px) {
      @content;
    }
  } @else {
    @error "Passed #{$min} breakpoint is not available for used pds-media-query-min() mixin.";
  }
}

@mixin pds-media-query-max($max: null) {
  @if map-has-key($pds-breakpoints, $max) {
    @media (max-width: #{map-get($pds-breakpoints, $max) - 1}px) {
      @content;
    }
  } @else {
    @error "Passed #{$max} breakpoint is not available for used pds-media-query-max() mixin.";
  }
}

@mixin pds-media-query-min-max($min: null, $max: null) {
  @if map-has-key($pds-breakpoints, $min) and map-has-key($pds-breakpoints, $max) {
    @media (min-width: #{map-get($pds-breakpoints, $min)}px) and (max-width: #{map-get($pds-breakpoints, $max) - 1}px) {
      @content;
    }
  } @else {
    @error "Passed #{$min} and/or #{$max} breakpoint is not available for used pds-media-query-min-max() mixin.";
  }
}

@import 'lib/border';
@import 'lib/theme';
@import 'lib/motion';

@mixin pds-skeleton($theme: 'light') {
  @if ($theme == 'dark') {
    background: $pds-theme-dark-background-surface
      linear-gradient(to right, transparent 0%, #1a1b1e 20%, transparent 50%)
      0
      0 /
      200%
      100%;
  } @else {
    background: $pds-theme-light-background-surface
      linear-gradient(to right, transparent 0%, #f7f7f7 20%, transparent 50%)
      0
      0 /
      200%
      100%;
  }

  display: block;
  border-radius: $pds-border-radius-small;

  animation: skeletonAnimation $pds-motion-duration-long $pds-motion-easing-base infinite;

  @keyframes skeletonAnimation {
    from {
      background-position-x: 100%;
    }
    to {
      background-position-x: -100%;
    }
  }
}

