// common type definitions
export type TextSize = 'x-small' | 'small' | 'medium' | 'large' | 'x-large' | 'inherit';

export type TextWeight = 'thin' | 'regular' | 'bold';

export type TextColor = 'porsche-black' | 'porsche-light' | 'porsche-red' | 'inherit';

export type ThemeColor = 'brand' | 'default' | 'neutral-1' | 'neutral-2' | 'neutral-3' | 'notification-success' | 'notification-warning' | 'notification-error' | 'inherit';

export type Theme = 'light' | 'dark';

export type ButtonType = 'button' | 'submit' | 'reset';

export type LinkTarget = '_self' | '_blank' | '_parent' | '_top' | string;
